import type { OpenApi, OpenApiV3 } from "@samchon/openapi";
export type IJsonSchemaCollection<Version extends "3.0" | "3.1" = "3.1"> =
  Version extends "3.0" ? IJsonSchemaCollection.IV3_0 : IJsonSchemaCollection.IV3_1;
export namespace IJsonSchemaCollection {
  export interface IV3_0 {
    version: "3.0";
    schemas: OpenApiV3.IJsonSchema[];
    components: OpenApiV3.IComponents;
